import React, { useState } from 'react';
import '../styles/Form.css';  // Стили для Form


const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    position: '',
    experience: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { username, email, password, position, experience } = formData;

    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password, position, experience }),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Пользователь зарегистрирован:', data);
      } else {
        console.log('Ошибка регистрации:', data.message);
      }
    } catch (error) {
      console.error('Ошибка при регистрации:', error);
    }
  };

  return (
    <div>
      <h1 className="title" >Регистрация</h1>
      <form className="base-form" onSubmit={handleSubmit}>
        <input className="form-input"
          type="text"
          name="username"
          value={formData.username}
          onChange={handleChange}
          placeholder="Имя пользователя"
        />
        <input className="form-input"
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Электронная почта"
        />
        <input 
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Пароль"
        />
        <input
          type="text"
          name="position"
          value={formData.position}
          onChange={handleChange}
          placeholder="Должность"
        />
        <input
          type="text"
          name="experience"
          value={formData.experience}
          onChange={handleChange}
          placeholder="Опыт"
        />
        <button type="submit">Зарегистрироваться</button>
      </form>
    </div>
  );
};

export default Register;
