import React, { useState } from 'react';
import '../styles/Form.css';

const Login = () => {
    // Состояния для хранения значений полей формы
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Функция для отправки формы
    const handleSubmit = (e) => {
        e.preventDefault();

        // Здесь можно отправить данные на сервер для авторизации
        console.log('Авторизация:', { email, password });
    };

    return  (
        <div className="form-container">
            <h1>Авторизация</h1>
            <form onSubmit={handleSubmit} className="form">
                <label className="F" htmlFor="email">Email:</label>
                <input className="F"
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    placeholder="Введите ваш email"
                />
                
                <label htmlFor="password">Пароль:</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    placeholder="Введите пароль"
                />
                
                <button type="submit" className="btn-submit">Войти</button>
            </form>
            <p className="register-link">Нет аккаунта? <a href="/register">Зарегистрироваться</a></p>
        </div>
    );
};

export default Login;
