import React, { useState } from 'react';
import '../styles/Form.css';

const Register = () => {
    // Состояния для хранения значений полей формы
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

 // Функция для отправки формы
 const handleSubmit = async (e) => {
    e.preventDefault();

    // Проверка совпадения паролей
    if (password !== confirmPassword) {
        alert('Пароли не совпадают');
        return;
    }

    // Отправка данных на сервер
    try {
        const response = await fetch('/api/auth/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, email, password }), // Отправляем имя, email и пароль
        });

        const data = await response.json();

        if (response.ok) {
            // Если регистрация успешна, сохраняем токен
            localStorage.setItem('token', data.token); // Сохраняем токен в локальном хранилище
            alert('Регистрация успешна');
            // Перенаправляем пользователя на другую страницу
            window.location.href = '/profile'; // или используйте react-router
        } else {
            alert(data.message || 'Ошибка при регистрации');
        }
    } catch (error) {
        console.error('Ошибка при отправке запроса:', error);
        alert('Что-то пошло не так');
    }
};

    return (
        <div className="register-form">
            <h1 className="form-title">Регистрация</h1>
            <form onSubmit={handleSubmit}>
                <label className="form-label" htmlFor="username">Имя:</label>
                <input className="form-input"
                    type="text"
                    id="username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    placeholder="Введите ваше имя"
                />
                
                <label className="form-label" htmlFor="email">Email:</label>
                <input className="form-input"
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    placeholder="Введите ваш email"
                />
                
                <label className="form-label" htmlFor="password">Пароль:</label>
                <input className="form-input"
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    placeholder="Введите пароль"
                />
                
                <label className="form-label" htmlFor="confirm-password">Подтвердите пароль:</label>
                <input className="form-input"
                    type="password"
                    id="confirm-password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                    placeholder="Повторите пароль"
                />
                
                <button className="form-button" type="submit">Зарегистрироваться</button>
            </form>
            <p className="login-link">Уже есть аккаунт? <a href="/login">Войти</a></p>
        </div>
    );
};

export default Register;
