import React, { useState } from 'react';
import '../styles/Form.css';

const Login = () => {
    // Состояния для хранения значений полей формы
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Функция для отправки формы
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Отправка данных на сервер
        try {
            const response = await fetch('/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json', // Указываем, что отправляем данные в формате JSON
                },
                body: JSON.stringify({ email, password }), // Тело запроса: данные формы
            });
        
            const data = await response.json();
        
            if (response.ok) {
                // Если запрос успешен, то сохраняем токен
                localStorage.setItem('token', data.token); // Сохраняем токен в локальном хранилище
                alert('Авторизация успешна');
                // Перенаправляем пользователя на другую страницу
                window.location.href = '/profile'; // или используйте react-router
            } else {
                alert(data.message || 'Ошибка при авторизации');
            }
        } catch (error) {
            console.error('Ошибка при отправке запроса:', error);
            alert('Что-то пошло не так');
        }
    };

    return  (
        <div className="form-container">
            <h1 className="form-title">Авторизация</h1>
            <form className="register-form" onSubmit={handleSubmit}>
                <label className="form-label" htmlFor="email">Email:</label>
                <input className="form-input"
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    placeholder="Введите ваш email"
                />
                
                <label className="form-label" htmlFor="password">Пароль:</label>
                <input className="form-input"
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    placeholder="Введите пароль"
                />
                
                <button type="submit" className="form-button">Войти</button>
            </form>
            <p className="register-link">Нет аккаунта? <a href="/register">Зарегистрироваться</a></p>
        </div>
    );
};

export default Login;
